		L[i] += L[i - 1];
		R[i] += R[i - 1];
		U[i] += U[i - 1];
		D[i] += D[i - 1];
	}
	int ans = n + 1;
	for(int i = 1, f = 0; i <= n; ++i, f = 0){
		int l = i, r = n;
		while(l <= r) {
			int mid = l + (r - l) / 2;
			if(!ok(i,mid)) {
				l = mid + 1;
			}else{
				r = mid - 1;
				f = 1;
			}
		}
		if(f) ans = min(ans,r - i + 2);
	}
	if(ans == n + 1) ans = -1;
	cout<<ans<<endl;
    return 0;
}
```

