bool ok(int s,int t)
{
	int ret = t - s + 1, tx = sx, ty = sy;
	tx += L[t] - L[s - 1];
	ty += D[t] - D[s - 1];
	tx -= R[t] - R[s - 1];
	ty -= U[t] - U[s - 1];
	if(abs(x - tx) + abs(y - ty) > ret) return 0;
	return (ret - abs(x - tx) - abs(y - ty)) % 2 == 0;
}

