    freopen("input.in","r",stdin);
#endif
	string s;
	cin>>n>>s>>x>>y;
	for(int i = 0; i < n; ++i) {
		if(s[i] == 'L') sx--, L[i + 1] = 1;
		if(s[i] == 'R') sx++, R[i + 1] = 1;
		if(s[i] == 'U') sy++, U[i + 1] = 1;
		if(s[i] == 'D') sy--, D[i + 1] = 1;
	}
	if(sx == x && sy == y) return cout<<0<<endl, 0;
	for(int i = 1; i <= n; ++i) {
		L[i] += L[i - 1];
		R[i] += R[i - 1];
		U[i] += U[i - 1];
		D[i] += D[i - 1];
	}
