#include <bits/stdc++.h>

using namespace std;
typedef long long LL;
const int N = 1e5+10;
const LL inf = 1e18;
LL n,m,k,s,a[N],sum[N];
bool vis[N];
vector<LL> v[N];

LL solve(int d,int n,LL m) {
    LL ans = 0;
    memset(sum, 0, sizeof sum);
    for (int i = 1; i <= n; ++i)
        sum[i] = sum[i - 1] + v[d][i - 1];
    for (int i = n + 1; i <= 2 * n; ++i)
        sum[i] = sum[i - 1] + v[d][i - n - 1];
    for (int i = 2 * n + 1; i <= 3 * n; ++i)
        sum[i] = sum[i - 1] + v[d][i - 2 * n - 1];

    deque<int> dq;

    for (int i = 1; i <= 3 * n; ++i) {
        while (!dq.empty() && sum[dq.back()] > sum[i - 1])
            dq.pop_back();
        while (!dq.empty() && dq.front() + m < i) //m
            dq.pop_front();
        dq.emplace_back(i - 1);
        ans = max(ans, sum[i] - sum[dq.front()]);
    }
    return ans;
}

int main() {

    int T;
    scanf("%d", &T);
    for (int t = 0; t < T; ++t) {
        memset(vis, 0, sizeof vis);
        LL ans = -inf;
        int tot = 0;
        for (int i = 0; i < N; ++i)
            v[i].clear();
        scanf("%lld%lld%lld%lld", &n, &s, &m, &k);
        for (int i = 0; i < n; i++) scanf("%lld", &a[i]);
        //
        for (int i = 0; i < n; i++) {
            if (!vis[i]) {
                for (int j = i; !vis[j]; j = (j + k) % n) {
                    vis[j] = 1;
                    v[tot].push_back(a[j]);
                }
                tot++;
            }
        }
        for (int i = 0; i < tot; ++i) {
            LL res = 0, tmp = 0;
            int len = v[i].size();
            for (int j = 0; j < len; ++j)
                tmp += v[i][j];
            res = solve(i, len, m);
            ans = max(ans, res);
            //0m
            if (tmp < 0)
                continue;
            LL ret = m % len;
            LL cnt = m / len;
            tmp = max(cnt - 1, 0LL) * tmp; // cnt-1
            if (cnt >= 1) ret += len;
            res = max(res, solve(i, len, ret) + tmp); //()
            ans = max(ans, res);
        }
        ans = max(s - ans, 0LL);
        printf("Case #%d: %lld\n", t + 1, ans);
    }
    return 0;
}
