#include <bits/stdc++.h>

#define lowbit(x) ((x)&(-(x)))
#define max(a, b) (((a)>(b))?(a):(b))
using namespace std;
const int N = 1e5 + 10;
struct node {
    int x, y, w;
} a[N];
int bit[N], shit[N];

bool cmp(const node &a, const node &b) {
    if (a.x == b.x)
        return a.y > b.y;
    return a.x < b.x;
}

void update(int x, int v) {
    for (int i = x; i < N; i += lowbit(i))
        bit[i] = max(v, bit[i]);
}

int query(int d) {
    int ans = 0;
    for (int i = d; i; i -= lowbit(i))
        ans = max(ans, bit[i]);
    return ans;
}

int main() {
    int T, n, x, y, w;
    scanf("%d", &T);
    while (T--) {
        memset(bit, 0, sizeof bit);
        vector<int> id;
        scanf("%d", &n);
        for (int i = 0; i < n; ++i) {
            scanf("%d%d%d", &x, &y, &w);
            a[i].x = x, a[i].y = y, a[i].w = w;
            id.push_back(y);
        }
        sort(id.begin(), id.end());
        id.erase(unique(id.begin(), id.end()), id.end());
        sort(a, a + n, cmp);
        for (int i = 0; i < n; ++i)
            shit[i] = lower_bound(id.begin(), id.end(), a[i].y) - id.begin() + 1;
        int love = 0;
        for (int i = 0; i < n; ++i) {
            //dp[i] = max{dp[0, i - 1]}+a[i].w;
            int fuck = query(shit[i] - 1) + a[i].w;
            update(shit[i], fuck);
            love = max(love, fuck);
        }
        printf("%d\n", love);
    }
}
